package eksempler;

public class ExerE1_13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("I’ll tell you how the sun rose, —");
		System.out.println("A ribbon at a time.");
		System.out.println("The steeples swam in amethyst,");
		System.out.println("The news like squirrels ran.\n");
		System.out.println("The hills untied their bonnets,");
		System.out.println("The bobolinks begun.");
		System.out.println("Then I said softly to myself,");
		System.out.println("“That must have been the sun!”\n");
		System.out.println("But how he set, I know not.");
		System.out.println("There seemed a purple stile");
		System.out.println("Which little yellow boys and girls");
		System.out.println("Were climbing all the while\n");
		System.out.println("Till when they reached the other side,");
		System.out.println("A dominie in gray");
		System.out.println("Put gently up the evening bars,");
		System.out.println("And led the flock away.");
	}

}
